// Arithmetic operators
let sum = 10 + 5; // 15
let product = 10 * 5; // 50

// Comparison operators
console.log(10 > 5); // true
console.log(10 === '10'); // false (strict equality)

// Logical operators
let isAdult = true;
let hasLicense = false;
console.log(isAdult && hasLicense); // false
console.log(isAdult || hasLicense); // true
